import { useState } from 'react';
import React from 'react';
import Navbar from "../components/navbar.jsx";
import styled from 'styled-components';
import TinderCard from 'react-tinder-card';
import { useDispatch } from 'react-redux'; // Import useDispatch

const Home = () => {
    const [lastDirection, setLastDirection] = useState(); // useState inside component
    const dispatch = useDispatch(); // useDispatch inside component

    const swiped = (direction, product) => {
        console.log('removing:', product.name);
        setLastDirection(direction);
        if (direction === 'up') {
            console.log('Adding to cart:', product); // Debug log
            dispatch({ type: 'ADD_TO_CART', payload: product });
        }
    };

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!');
    };

    const HeroBody = styled.div`
        display: grid;
        grid-template-columns: repeat(12, 1fr);
    `;

    const HeroText = styled.h1`
        font-size: 96px;
        grid-column: 2 / 7;
    `;

    const DisplayCard = styled.div`
        grid-column: 7 / 13;
    `;

    // Assuming `characters` is an array of products you want to display
    const characters = [
        { name: 'Product 1', url: 'https://example.com/product1.jpg' },
        { name: 'Product 2', url: 'https://example.com/product2.jpg' },
        // Add more products here
    ];

    return (
        <>
            <Navbar AuthToken />
            <HeroBody>
                <HeroText>
                    Swipe Your Way
                    To Shop
                </HeroText>
                <DisplayCard>
                    <div className="swiper-container">
                        <div className="card-container">
                            {characters.map((character) => (
                                <TinderCard
                                    className='swipe'
                                    key={character.name}
                                    onSwipe={(dir) => swiped(dir, character)}
                                    onCardLeftScreen={() => outOfFrame(character.name)}
                                >
                                    <div
                                        style={{
                                            backgroundImage: `url(${character.url})`
                                        }}
                                        className='card'
                                    >
                                        <h3 className='name'>{character.name}</h3>
                                    </div>
                                </TinderCard>
                            ))}
                        </div>
                    </div>
                </DisplayCard>
            </HeroBody>
        </>
    );
};

export default Home;
