import React, { useState } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import TinderCard from 'react-tinder-card';
import Navbar from "../components/navbar.jsx";
import About from "../pages/About.jsx";
import { useDispatch } from 'react-redux';

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Convergence&display=swap');
`;

const Home = () => {
    const [lastDirection, setLastDirection] = useState();
    const dispatch = useDispatch();

    const swiped = (direction, product) => {
        setLastDirection(direction);
        if (direction === 'up') {
            dispatch({ type: 'ADD_TO_CART', payload: product });
        }
    };

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!');
    };

    const HeroBody = styled.div`
    display: grid;
    grid-template-columns: repeat(12, 1fr);
    grid-template-rows: repeat(100, 8px);
  `;

    const CardContainer = styled.div`
    grid-column: 9 / 11;
    grid-row: 10 / 20;
  `;

    const ShopButton = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1em;
    background-color: black;
    height: 50px;
    width: 200px;
    grid-column: 2;
    border-radius: 40px;
    grid-row: 50 / 60;
    position: relative;

    button {
      font-size: 1.2em;
      background-color: transparent;
      color: white;
      border: none;
      cursor: pointer;
      outline: none;
    }

    button:hover {
      color: #ccc;
    }
  `;

    const HeroText = styled.h1`
    font-size: 96px;
    grid-column: 2 / 7;
    grid-row: 10 / 20;
    font-family: 'Convergence', sans-serif;
  `;

    const DisplayCard = styled.div`
    grid-column: 7 / 13;
  `;

    const characters = [
        // Product data
    ];

    return (
        <>
            <Navbar />
            <HeroBody>
                <HeroText>Swipe Your Way To Shop</HeroText>

                <CardContainer>
                    {characters.map((character) => (
                        <TinderCard
                            key={character.name}
                            onSwipe={(dir) => swiped(dir, character)}
                            onCardLeftScreen={() => outOfFrame(character.name)}
                        >
                            <div style={{ backgroundImage: `url(${character.url})` }} className="card" />
                        </TinderCard>
                    ))}
                </CardContainer>

                <ShopButton>
                    <button type="submit">Shop</button>
                </ShopButton>
            </HeroBody>

            {/* About Section will appear directly under Home content */}
            <About />
        </>
    );
};

export default Home;
